package day03;

import javax.swing.JOptionPane;

public class Testfor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 5; i++) {
			System.out.println(i);
				
			}
		
		int j = 0;
		while(j<5) {
			System.out.println("while="+j);
			j++;
		}
		int k=0;
		do {
			System.out.println("do while"+k);
			k++;
		} while (k<5);

	}

}
